// Dans votre fonction 'Traiter Résultats'

// --- Initial Check for Flask Errors ---
// Check if payload exists and has a status field first
if (!msg.payload || typeof msg.payload !== 'object') {
    node.error("Erreur API: Réponse invalide ou non-objet reçue de Flask.");
    const errorMsg = {
        payload: {
            timestamp: new Date().toISOString(),
            status: 'error',
            message: 'Invalid or non-object response from model service.',
            details: msg.payload
        }
    };
    flow.set('anomalyCount', 0);
    return [null, errorMsg];
}

// Check specifically for the error status from Flask
if (msg.payload.status !== 'success') {
    node.error("Erreur API Flask: " + (msg.payload.message || 'Message d\'erreur vide'));
    const errorMsg = {
        payload: {
            timestamp: new Date().toISOString(),
            status: 'error',
            message: 'Error reported by model service',
            details: msg.payload.message || JSON.stringify(msg.payload)
        }
    };
    flow.set('anomalyCount', 0);
    return [null, errorMsg];
}

// --- Process Successful Response ---
const res = msg.payload; // Response from Flask is successful

// Create a new clean message for the dashboard/further processing
const newMsg = {
    payload: {
        timestamp: res.prediction_for_timestamp || res.sequence_end_timestamp || new Date().toISOString(),
        metrics: {
            dc_power: res.last_values_in_sequence_raw.DC_POWER,
            ac_power: res.last_values_in_sequence_raw.AC_POWER,
            ambient_temp: res.last_values_in_sequence_raw.AMBIENT_TEMPERATURE,
            module_temp: res.last_values_in_sequence_raw.MODULE_TEMPERATURE,
            irradiation: res.last_values_in_sequence_raw.IRRADIATION,
            temperature_diff: res.last_values_in_sequence_raw.MODULE_TEMPERATURE - res.last_values_in_sequence_raw.AMBIENT_TEMPERATURE
        },
        prediction_error_scaled: res.mae_scaled,
        anomaly_threshold_scaled: res.anomaly_threshold_scaled,
        status: res.is_anomaly ? 'warning' : 'normal',
        predicted_values_orig: res.predicted_next_values_orig,
        actual_next_values_raw: res.actual_next_values_raw
    }
};

// --- Anomaly Severity Calculation ---
let severity_above_threshold = 0;
if (res.is_anomaly && res.mae_scaled !== undefined && res.anomaly_threshold_scaled !== undefined) {
    severity_above_threshold = res.mae_scaled - res.anomaly_threshold_scaled;
}

// --- Consecutive Anomaly Logic ---
let anomalyCount = flow.get('anomalyCount') || 0;

if (res.is_anomaly) {
    anomalyCount++;
    flow.set('anomalyCount', anomalyCount);

    // --- Determine Risk Level Based on Combined Thresholds ---
    let riskLevel = 0;
    let riskMessage = "Système Normal";
    let timeIndication = "Aucun risque imminent";
    let alertLevel = "info";

    // Define thresholds (you can adjust these values based on your data)
    const LOW_SEVERITY_THRESHOLD = 0.05;  // Small deviation above threshold
    const MEDIUM_SEVERITY_THRESHOLD = 0.15; // Moderate deviation
    const HIGH_SEVERITY_THRESHOLD = 0.3;   // Large deviation

    // Level 1: Mild Anomaly / Monitoring
    if (severity_above_threshold <= LOW_SEVERITY_THRESHOLD && anomalyCount <= 3) {
        riskLevel = 1;
        riskMessage = "Anomalie Détectée - Surveillance Recommandée";
        timeIndication = "Risque potentiel à long terme (semaines)";
        alertLevel = "warning";
        newMsg.payload.status = 'warning';
    } 
    // Level 2: Moderate Anomaly / Attention
    else if ((severity_above_threshold > LOW_SEVERITY_THRESHOLD && severity_above_threshold <= MEDIUM_SEVERITY_THRESHOLD) || 
             (anomalyCount > 3 && anomalyCount <= 10)) {
        riskLevel = 2;
        riskMessage = "Comportement Anormal - Attention Requise";
        timeIndication = "Risque potentiel dans les jours à venir (< 72h)";
        alertLevel = "warning";
        newMsg.payload.status = 'warning';
    } 
    // Level 3: Severe Anomaly / Critical
    else if (severity_above_threshold > MEDIUM_SEVERITY_THRESHOLD || anomalyCount > 10) {
        riskLevel = 3;
        riskMessage = "ALERTE CRITIQUE - Risque de Défaillance";
        timeIndication = "Défaillance probable dans les 24-48h si non résolue";
        alertLevel = "critical";
        newMsg.payload.status = 'critical';
    }

    // Add severity information to the message
    newMsg.payload.anomaly_severity = {
        level: riskLevel,
        severity_above_threshold: severity_above_threshold,
        consecutive_count: anomalyCount
    };

    // Create alert message
    newMsg.alert = {
        level: alertLevel,
        message: `${riskMessage} (Niveau ${riskLevel})`,
        details: `Déviation: ${severity_above_threshold.toFixed(4)} au-dessus du seuil (${anomalyCount} anomalies consécutives). ${timeIndication}. Prédiction pour ${newMsg.payload.timestamp}.`,
        technical_details: {
            mae_scaled: res.mae_scaled,
            threshold: res.anomaly_threshold_scaled,
            severity_above_threshold: severity_above_threshold,
            consecutive_anomalies: anomalyCount
        }
    };

    // Send anomaly messages to output 1
    return [newMsg, null];
} else {
    // --- Normal Operation ---
    flow.set('anomalyCount', 0);
    newMsg.payload.status = 'normal';

    // Add normal level information
    newMsg.payload.anomaly_severity = {
        level: 0,
        severity_above_threshold: 0,
        consecutive_count: 0
    };

    // Optional normal status message
    newMsg.alert = {
        level: "info",
        message: "Système Normal",
        details: "Aucune anomalie détectée. Toutes les métriques sont dans les limites normales."
    };

    // Send normal messages to output 2
    return [null, newMsg];
}
